  <script>
    /********* VARIABLES & INITIALIZATION **********/
    const editor = document.getElementById('editor');
    const highlighting = document.getElementById('highlighting');
    const lineNumbers = document.getElementById('line-numbers');
    const statusBar = document.getElementById('status');
    const languageSelect = document.getElementById('language-select');

    // For find/replace
    const findPanel = document.getElementById('find-panel');
    const findInput = document.getElementById('find-input');
    const replaceInput = document.getElementById('replace-input');
    let currentFindIndex = 0;

    // Load autosaved content if available
    if(localStorage.getItem('advancedNotepadContent')) {
      editor.value = localStorage.getItem('advancedNotepadContent');
    }
    updateEditor();

    /********* EVENT LISTENERS **********/

    // Update highlighting and line numbers on input
    editor.addEventListener('input', () => {
      updateEditor();
      autoSave();
    });

    // Sync scroll between textarea, pre, and line numbers
    editor.addEventListener('scroll', () => {
      highlighting.scrollTop = editor.scrollTop;
      lineNumbers.scrollTop = editor.scrollTop;
    });

    // Auto-indentation on Enter key
    editor.addEventListener('keydown', (e) => {
      if(e.key === 'Enter') {
        const start = editor.selectionStart;
        const value = editor.value;
        let indent = '';
        // Find the previous line
        const lineStart = value.lastIndexOf('\n', start - 1) + 1;
        const line = value.substring(lineStart, start);
        const match = line.match(/^\s+/);
        if(match) { indent = match[0]; }
        setTimeout(() => {
          const pos = editor.selectionStart;
          editor.value = editor.value.substring(0, pos) + indent + editor.value.substring(pos);
          editor.selectionStart = editor.selectionEnd = pos + indent.length;
          updateEditor();
        }, 0);
      }
      // Save Shortcut Ctrl+S
      if((e.ctrlKey || e.metaKey) && e.key === 's') {
        e.preventDefault();
        saveFile();
      }
    });

