    });
    document.getElementById('find-next-btn').addEventListener('click', findNext);
    document.getElementById('replace-btn').addEventListener('click', doReplace);
    document.getElementById('replace-all-btn').addEventListener('click', doReplaceAll);

    /********* FUNCTIONS **********/

    function updateEditor() {
      const code = editor.value;
      updateLineNumbers(code);
      updateStatus(code);
      const lang = languageSelect.value;
      const highlightedCode = syntaxHighlight(code, lang);
      highlighting.innerHTML = highlightedCode;
    }

    function updateLineNumbers(text) {
      const lines = text.split('\n').length;
      let lineHtml = '';
      for(let i=1;i<=lines;i++){
        lineHtml += i + '<br>';
      }
      lineNumbers.innerHTML = lineHtml;
    }

    function updateStatus(text) {
      const lines = text.split('\n').length;
      const chars = text.length;
      statusBar.textContent = `Lines: ${lines} | Characters: ${chars}`;
    }

    // Basic syntax highlighter
    function syntaxHighlight(text, lang) {
      // Escape HTML
      text = text.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');

      if(lang === 'plaintext'){
        return text;
      }
      else if(lang === 'javascript'){
        // Comments: //... and /* ... */
        text = text.replace(/(\/\*[\s\S]*?\*\/)|(\/\/.*$)/gm, match => '<span class="token comment">'+match+'</span>');
        // Strings: "..." or '...'
        text = text.replace(/("([^"\\]|\\.)*")|('([^'\\]|\\.)*')/g, match => '<span class="token string">'+match+'</span>');
        // Keywords
        const keywords = ["break", "case", "catch", "class", "const", "continue", "debugger", "default", "delete", "do", "else", "export", "extends", 
                          "finally", "for", "function", "if", "import", "in", "instanceof", "let", "new", "return", "super", "switch", "this", 
                          "throw", "try", "typeof", "var", "while", "with", "yield"];
        const kwRegex = new RegExp('\\b('+keywords.join('|')+')\\b', 'g');
        text = text.replace(kwRegex, '<span class="token keyword">$1</span>');
        // Numbers
        text = text.replace(/\b(\d+(\.\d+)?)\b/g, '<span class="token number">$1</span>');
      }
      else if(lang === 'html'){
        // Simple tag highlighting: tags, attributes and attribute values
        text = text.replace(/(&lt;!--[\s\S]*?--&gt;)/g, '<span class="token comment">$1</span>');
        text = text.replace(/(&lt;\/?[a-zA-Z]+)([^&]*?)(&gt;)/g, function(match, p1, p2, p3) {
          // Attributes within a tag:
          p2 = p2.replace(/([a-zA-Z-:]+)(=)("[^"]*"|'[^']*')/g, '<span class="token attr">$1</span>$2<span class="token attr-value">$3</span>');
          return '<span class="token tag">'+p1+'</span>' + p2 + '<span class="token tag">'+p3+'</span>';
        });
      }
      else if(lang === 'css'){
        // Comments
        text = text.replace(/(\/\*[\s\S]*?\*\/)/g, '<span class="token comment">$1</span>');
        // Selectors and property names (very basic)
        text = text.replace(/([.#]?[a-zA-Z0-9_-]+)(\s*\{)/g, '<span class="token keyword">$1</span>$2');
        // Strings
        text = text.replace(/("([^"\\]|\\.)*")|('([^'\\]|\\.)*')/g, match => '<span class="token string">'+match+'</span>');
        // Numbers
        text = text.replace(/\b(\d+(\.\d+)?)(px|em|rem|%)?\b/g, '<span class="token number">$1</span>');
      }

      return text;
    }

    function autoSave() {
      localStorage.setItem('advancedNotepadContent', editor.value);
    }

    function saveFile() {
      const blob = new Blob([editor.value], {type: "text/plain;charset=utf-8"});
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = "note.txt";
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);
    }

    /********* FIND/REPLACE FUNCTIONS **********/
    function findNext() {
      const searchText = findInput.value;
      if(!searchText) return;
      const content = editor.value;
      currentFindIndex = content.indexOf(searchText, editor.selectionEnd);
      if(currentFindIndex === -1) {
        alert("No further matches.");
        return;
      }
      editor.focus();
      editor.selectionStart = currentFindIndex;
      editor.selectionEnd = currentFindIndex + searchText.length;
    }

    function doReplace() {
      const searchText = findInput.value;
      const replaceText = replaceInput.value;
      if(!searchText) return;
      const selStart = editor.selectionStart;
      const selEnd = editor.selectionEnd;
      if(editor.value.substring(selStart, selEnd) === searchText) {
        // Replace the current selection:
        const before = editor.value.substring(0, selStart);
        const after = editor.value.substring(selEnd);
        editor.value = before + replaceText + after;
        editor.selectionStart = selStart;
        editor.selectionEnd = selStart + replaceText.length;
        updateEditor();
      }
      findNext();
    }

    function doReplaceAll() {
      const searchText = findInput.value;
      const replaceText = replaceInput.value;
      if(!searchText) return;
      const regex = new RegExp(searchText, "g");
      editor.value = editor.value.replace(regex, replaceText);
      updateEditor();
    }

    /********* AUTO SAVE EVERY 3 SECONDS **********/
    setInterval(autoSave, 3000);

  </script>
</body>
</html>
