    AudioCapture = NewObject<UAudioCapture>(this);
}

void AMyGameMode::BeginPlay()
{
    Super::BeginPlay();

    // Initialize and start capturing audio
    InitializeAudioCapture();
    StartCapture();
}

void AMyGameMode::InitializeAudioCapture()
{
    // Set audio parameters (adjust as needed)
    const int32 SampleRate = 48000;
    const int32 BufferSize = 8192;

    bool bInitialized = AudioCapture->Initialize(SampleRate, BufferSize);
    if (bInitialized)
    {
        UE_LOG(LogTemp, Log, TEXT("AudioCapture initialized successfully"));
    }
    else
    {
        UE_LOG(LogTemp, Error, TEXT("Failed to initialize AudioCapture"));
        return;
    }

    // Bind the audio data callback
    AudioCaptureDelegateHandle = 
        AudioCapture->OnAudioCaptureBuffer.AddUObject(this, &AMyGameMode::OnAudioReceived);
}

void AMyGameMode::OnAudioReceived(const float* AudioData, int32 NumSamples, int32 SampleRate)
{
